// n8n Function Node Code
// Returns [{ json: { date, variant, audience, cycleDay, topic } }, ‚Ä¶]

const startDate = new Date('2025-05-15'); // adjust as needed
const numDays   = 7;                      // number of days to generate
const msPerDay  = 24 * 60 * 60 * 1000;

// 1) Day-of-week topic map
const dayTopics = {
  1: 'Motivational',      // Monday
  2: 'News Article',      // Tuesday
  3: 'Authentic Story',   // Wednesday
  4: 'Behind the Scenes', // Thursday
  5: 'Reflection',        // Friday
  6: 'Personal',          // Saturday
  0: 'Q&A',               // Sunday
};

// 2) Compute schedule for one date
function getScheduleForDate(date, origin) {
  const daysSinceStart = Math.floor((date - origin) / msPerDay);

  // A/B test variant: even = image, odd = text
  const variant = (daysSinceStart % 2 === 0) ? 'image' : 'text';

  // Rotate audience every 4 days
  const audiences = ['Owners Rep', 'Owners', 'Captains', 'Brokers'];
  const audience = audiences[Math.floor(daysSinceStart / 4) % audiences.length];

  // Cycle index for reference
  const cycleDay = daysSinceStart + 1;

  // Topic by weekday
  const weekday = date.getDay(); // 0=Sunday,1=Monday...
  const topic = dayTopics[weekday];

  return { variant, audience, cycleDay, topic };
}

// 3) Build output array
const output = [];
for (let i = 0; i < numDays; i++) {
  const currentDate = new Date(startDate.getTime() + i * msPerDay);
  const { variant, audience, cycleDay, topic } = getScheduleForDate(currentDate, startDate);
  output.push({
    json: {
      date: currentDate.toISOString().split('T')[0],
      variant,
      audience,
      cycleDay,
      topic,
    }
  });
}

return output;

